targets.expr:
sum by(node, pod, device) (
  rate(
    node_disk_written_bytes_total[$__rate_interval]
  )
)
+
ignoring(pod) group_right() (
  label_replace(
    count by(
      node,
      pod,
      device
    )
    (
      container_fs_writes_bytes_total{
        cluster_id=~"$cluster", namespace=~"$namespace",
        container=~"distributor|ingester|mimir-write",
        device!~".*sda.*"
      }
    ),
    "device",
    "$1",
    "device",
    "/dev/(.*)"
  ) * 0
)

