targets.expr:
sum(rate(nginx_ingress_controller_requests{
    cluster_type=~"management_cluster",
   ingress=~"prometheus-($cluster)-remote-write",
    status!~"[4-5].*"}[$__rate_interval]
)) by (ingress, host, path, method) / 
sum(rate(nginx_ingress_controller_requests{
    cluster_type=~"management_cluster",
    ingress=~"prometheus-($cluster)-remote-write"}[$__rate_interval]
)) by (ingress, host, path, method)

sum(irate(nginx_ingress_controller_requests{
    cluster_type=~"management_cluster",
    ingress=~"prometheus-($cluster)-remote-write",
    status!~"[2].*"}[$__rate_interval]
)) by (ingress, host, path, method, status)

(
  prometheus_remote_storage_highest_timestamp_in_seconds{cluster_id=~"$cluster", instance=~"$instance"} 
-  
  ignoring(remote_name, url) group_right(instance) (prometheus_remote_storage_queue_highest_sent_timestamp_seconds{cluster_id=~"$cluster", instance=~"$instance", url=~"$url"} != 0)
)
